mapper.insert:
INSERT INTO `subscribe`(`groupID`,`userID`,`inviteCode`,`joinDate`)
VALUES((SELECT `groupID` from `group` WHERE manager=${userID} ORDER BY groupID DESC LIMIT 1),
    #{userID},left(replace(uuid(),"-",""),12),CURRENT_TIMESTAMP);

